mapper.select:
SELECT PRODUCT_ID ,PRODUCT_CATEGORY ,PRODUCT_CATEGORY_CHILD ,PRODUCT_NAME ,PRODUCT_STATUS FROM product WHERE 1 = 1
AND PRODUCT_CATEGORY = #{productCategory}
AND PRODUCT_CATEGORY_CHILD = #{productCategoryChild}
AND PRODUCT_ID = #{productId}
ORDER BY PRODUCT_ID

mapper.update:
UPDATE product
SET
    PRODUCT_NAME = #{productName}
    ,PRODUCT_CATEGORY = #{productCategory}
    ,PRODUCT_CATEGORY_CHILD = #{productCategoryChild}
    ,PRODUCT_STATUS = #{productStatus}
WHERE PRODUCT_ID = #{productId}
